function getQueryParams(){let e=location.search;if(-1!=e.indexOf("?")){let t=e.substr(1).split("&"),a=0;for(i in t){let e=t[i].split("="),r=decodeURIComponent(e[1]);switch(e[0]){case"public_key":document.getElementById("area_public_key").value=r,a++;break;case"private_key":document.getElementById("area_private_key").value=r,a++;break;case"original_text":document.getElementById("area_original_text").value=r,a++;break;case"cipher_text":document.getElementById("area_cipher_text").value=r,a++}}a||generateKey()}else generateKey()}getQueryParams();